import './App.css';
import './css/header.css';
//import Modal from 'modal';
import React, { useState } from 'react';
import Modal from 'react-modal';


const [modalIsOpen, setModalIsOpen] = useState(false);
const openModal = () => {
  setModalIsOpen(true);
};

const closeModal = () => {
  setModalIsOpen(false);
};

const modalContent = (
  <div>
    <h2>Заголовок модального окна</h2>
    <p>Текст модального окна</p>
    <button onClick={closeModal}>Закрыть</button>
  </div>
);

function Btn() {
  return (
    <div>
      <button onClick={openModal}>Открыть модальное окно</button>
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
        {modalContent}
      </Modal>
    </div>
  );
}



export default function Header() {
  return (
    <div className="Header">
      <div className='Logo'>MONKEY SCRIPT</div>

      <ul id="yakors">
        <li><a href="#main2"  title="Link" >Примеры работ</a></li>
        <li><a href="#main3"  title="Link" >Тарифы</a></li>
        <li><a href="#main4"  title="Link" >Контакты</a></li>
      </ul>

      <ul id="regent">
        <li><a href="Enter.html" target="_blank" title="Link" className="btn">Вход</a></li>
        <li><Btn /></li>
      </ul>

    </div>
  );
}
