import React from 'react';
import './App.css';
import './css/header.css';

const Modal = ({ isVisible = false, title, content, footer, onClose }) => {
  const keydownHandler = ({ key }) => {
    switch (key) {
      case 'Escape':
        onClose();
        break;
      default:
    }
  };

  React.useEffect(() => {
    document.addEventListener('keydown', keydownHandler);
    return () => document.removeEventListener('keydown', keydownHandler);
  });

  return !isVisible ? null : (
    <div className="modal" onClick={onClose}>
      <div className="modal-dialog" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h3 className="modal-title">{title}</h3>
          <span className="modal-close" onClick={onClose}>
            &times;
          </span>
        </div>
        <div className="modal-body">
          <div className="modal-content">{content}</div>
        </div>
        {footer && <div className="modal-footer">{footer}</div>}
      </div>
    </div>
  );
};




export default function Header() {
  const [isModal, setModal] = React.useState(false);
  return (
    <div className="Header">
      <div className='Logo'>MONKEY SCRIPT</div>

      <ul id="yakors">
        <li><a href="#main2"  title="Link" >Примеры работ</a></li>
        <li><a href="#main3"  title="Link" >Тарифы</a></li>
        <li><a href="#main4"  title="Link" >Контакты</a></li>
      </ul>

      <ul id="regent">
        <li><a href="Enter.html" target="_blank" title="Link" className="btn">Вход</a></li>
        <li><button onClick={() => setModal(true)}>Регистрация</button>
        <Modal

          isVisible={isModal}
          title="РЕГИСТРАЦИЯ"
          content={<div>Add your content here</div>}
          footer={<button>Cancel</button>}
          onClose={() => setModal(false)}
        /></li>
      </ul>

    </div>
  );
}
